const App: FunctionComponent = () => { // typé App en React.FC (function component) = composant que l'on peut ecrir sous la forme d'une fonction
//  const name: String = 'React';
const [name, setName] = useState('React');
 return (
  <h1>Bonjour, {name} !</h1>
 )
}
  
export default App;

